import React, { useState } from 'react';
import TransactionForm from './components/TransactionForm';
import TransactionList from './components/TransactionList';

const App: React.FC = () => {
  const [transactions, setTransactions] = useState<{ description: string; amount: number }[]>([]);

  const handleTransactionSubmit = (transaction: { description: string; amount: number }) => {
    setTransactions([...transactions, transaction]);
  };

  return (
    <div className='container'>
      <div className='row'>
        <div className='col'> <TransactionForm onSubmit={handleTransactionSubmit} /></div>
        <div className='col'>      <TransactionList transactions={transactions} />
</div>
      </div>

    </div>
  );
};

export default App;
