import React, { useEffect, useState } from 'react';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';

// Interface for a transaction
interface Transaction {
  id: string;
  account: string;
  amount: number;
  type: "deposit" | "withdrawal";
  created_at: string;
}

// Interface for form data for creating a transaction
interface TransactionFormData {
  account_id: string;
  amount: number;
}

// The main component for the Home page
const Home = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);
  const [currentBalance, setCurrentBalance] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/transactions');
        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        const data = await response.json();
        setTransactions(data); // Assuming your response is an array of transactions
        setCurrentBalance(
          data.reduce((acc: number, transaction: Transaction) =>
            transaction.type === 'deposit' ? acc + transaction.amount : acc - transaction.amount,
            0
          )
        );
      } catch (error) {
        console.error('Error fetching transactions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);


  const addTransaction = async (transactionData: TransactionFormData) => {
    try {
      const response = await fetch('api/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const newTransaction: Transaction = await response.json();

      setTransactions(prevTransactions => [...prevTransactions, newTransaction]);
      setCurrentBalance(prevBalance =>
        newTransaction.type === 'deposit' ? prevBalance + newTransaction.amount : prevBalance - newTransaction.amount
      );
    } catch (error) {
      console.error('Error submitting transaction:', error);
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Home container mt-5">
      <div className='row'>
        <div className='col'>
          <TransactionForm onSubmit={addTransaction} />
        </div>
        <div className='col'>
          <TransactionList transactions={transactions} currentBalance={currentBalance} />
        </div>
      </div>
    </div>
  );
};

export default Home;
