import React, { useEffect, useState } from 'react';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';

type Transaction = {
  id: string;
  account: string;
  amount: number;
  type: string; // Assuming 'type' is a string, adjust as needed based on your API
  created_at: string;
};

type TransactionFormData = {
  account_id: string;
  amount: number;
};

const Home = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/data');
        const data = await response.json();
        setTransactions(data.transactions || []);
        // Calculate the current balance based on fetched transactions
        setCurrentBalance(data.transactions.reduce((acc: number, transaction: Transaction) => acc + transaction.amount, 0));
      } catch (error) {
        console.error('Error fetching transactions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const addTransaction = async (transactionData: TransactionFormData) => {
    try {
      const response = await fetch('/api/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const newTransaction: Transaction = await response.json();

      setTransactions([...transactions, newTransaction]);
      // Update the current balance
      setCurrentBalance((prevBalance) => prevBalance + newTransaction.amount);
    } catch (error) {
      console.error('Error submitting transaction:', error);
    }
  };

  const [currentBalance, setCurrentBalance] = useState(0);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Home container mt-5">
      <div className='row'>
        <div className='col'>
          <TransactionForm onSubmit={addTransaction} />
        </div>
        <div className='col'>
          <TransactionList transactions={transactions} currentBalance={currentBalance} />
        </div>
      </div>
    </div>
  );
};

export default Home;
