import React, { useEffect, useState } from 'react';
import TransactionForm from './TransactionForm';
import TransactionList from './TransactionList';

const Home = () => {
  const [transactions, setTransactions] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/data');
        const data = await response.json();
        setTransactions(data.transactions || []); // Assuming the data structure includes a transactions field
      } catch (error) {
        console.error('Error fetching transactions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  return (

<div className="Home container mt-5">
<div className='row'>
    <div className='col'>      <TransactionForm onSubmit={addTransaction} />
</div>
    <div className='col'>      <TransactionList transactions={transactions} currentBalance={currentBalance} />
</div>
</div>
</div>
  );
};

export default Home;
