import React, { useEffect, useState } from 'react';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';

interface Transaction {
    transaction_id: string;
    account_id: string;
    amount: number;
    created_at: string;
  }

// The main component for the Home page
const Home = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [loading, setLoading] = useState(true);
  const [accountId, setAccountId] = useState<string>('');
  const [amount, setAmount] = useState<number>(0);
  const [balance, setBalance] = useState<number>(0);

  const fetchTransactions = async () => {
    try {
      const response = await fetch('http://localhost:3000/transactions');
      if (response.ok) {
        const data = await response.json();
        setTransactions(data);
      } else {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
    } catch (error) {
      console.error('Error fetching transactions:', error);
    }
  };

  const fetchBalance = async () => {
    try {
      const response = await fetch('http://localhost:3000/balance');
      if (response.ok) {
        const data = await response.json();
        setBalance(data.balance);
      } else {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }
    } catch (error) {
      console.error('Error fetching balance:', error);
    }
  };

  const submitTransaction = async (event: React.FormEvent) => {
    event.preventDefault();
    try {
      const response = await fetch('http://localhost:3000/transaction', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ account_id: accountId, amount: amount.toString() }), // Convert amount to string
      });

      if (response.ok) {
        fetchTransactions();
        fetchBalance();
      } else {
        const errorText = await response.text();
        console.error('Transaction submission failed');
      }
    } catch (error) {
      console.error('An error occurred:', error);
    }
  };
  useEffect(() => {
    fetchTransactions();
    fetchBalance();
  }, []);


  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Home container mt-5">
      <div className='row'>
        <div className='col'>
          <TransactionForm onSubmit={submitTransaction} />
        </div>
        <div className='col'>
          <TransactionList transactions={transactions} balance={balance} />
        </div>
      </div>
    </div>
  );
};

export default Home;
