import React, { useEffect, useState } from 'react';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';

const Home = () => {
  const [transactions, setTransactions] = useState([]);
  const [currentBalance, setCurrentBalance] = useState(0);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/api/data');
        const data = await response.json();
        setTransactions(data.transactions || []); // Assuming the data structure includes a transactions field
        // If your API returns the current balance, you can set it here
        // setCurrentBalance(data.currentBalance);
      } catch (error) {
        console.error('Error fetching transactions:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  const addTransaction = async (transactionData) => {
    try {
      const response = await fetch('/api/transactions', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(transactionData),
      });

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`);
      }

      const newTransaction = await response.json();

      setTransactions([...transactions, newTransaction]);
      // Optionally, you can calculate the new balance if your API doesn't do this
      // setCurrentBalance(currentBalance + newTransaction.amount);
    } catch (error) {
      console.error('Error submitting transaction:', error);
      // You might want to display this error to the user
    }
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  return (
    <div className="Home container mt-5">
      <div className='row'>
        <div className='col'>
          <TransactionForm onSubmit={addTransaction} />
        </div>
        <div className='col'>
          <TransactionList transactions={transactions} currentBalance={currentBalance} />
        </div>
      </div>
    </div>
  );
};

export default Home;
