import React, { useState } from 'react';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';

// Define a type for transactions if not already defined
type TransactionType = 'deposit' | 'withdrawal';

interface Transaction {
  id: string;
  amount: number;
  account: string;
  type: TransactionType;
}

const Home: React.FC = () => {
  const [transactions, setTransactions] = useState<Transaction[]>([]);
  const [currentBalance, setCurrentBalance] = useState<number>(0);

  const addTransaction = (accountId: string, amount: number) => {
    // Here you would typically handle the transaction, e.g., by calling an API
    // For this example, let's just update the local state
    const newTransaction: Transaction = {
      id: Date.now().toString(), // A mock ID, typically you'd get this from your backend
      account: accountId,
      amount,
      type: amount < 0 ? 'withdrawal' : 'deposit', // Determine type based on amount
    };

    // Update transactions
    setTransactions([...transactions, newTransaction]);

    // Update current balance
    setCurrentBalance(currentBalance + amount);
  };

  return (
    <div className="Home container">
      <TransactionForm onSubmit={addTransaction} />
      <TransactionList transactions={transactions} currentBalance={currentBalance} />
    </div>
  );
};

export default Home;
